import { fakerDE as faker } from '@faker-js/faker'

type Chat = { id: string; name: string; message: string }

export function useChat() {
  const chats = ref<Chat[]>([])

  function getChat(): Promise<Chat> {
    return new Promise((resolve) => {
      setTimeout(
        () =>
          resolve({
            id: faker.string.uuid(),
            name: faker.person.fullName(),
            message: faker.lorem.sentence(),
          }),
        500 // 0.5 seconds to respond
      )
    })
  }

  function pollingChats() {
    const INTERVAL = 2000 // 2 seconds
    setInterval(async () => {
      const chat = await getChat()
      chats.value.push(chat)
    }, INTERVAL)
  }

  return { chats, getChat, pollingChats }
}

export function useAutoScroll(container: Ref<HTMLElement | undefined>) {
  function scrollToBottom() {
    nextTick(() => {
      if (!container.value) {
        return
      }

      container.value.scrollTo(0, container.value.scrollHeight)
    })
  }

  return { scrollToBottom }
}

export function useAutoScroll(container: Ref<HTMLElement | undefined>) {
  const isAutoScrolling = ref<boolean>(true)

  watch(
    () => container.value,
    () => {
      if (container.value) {
        container.value.addEventListener('scroll', () => {
          const scrollTop = container.value!.scrollTop
          const scrollHeight = container.value!.scrollHeight
          const clientHeight = container.value!.clientHeight

          const reachBottom = scrollTop + clientHeight >= scrollHeight

          if (reachBottom) {
            isAutoScrolling.value = true
          } else {
            isAutoScrolling.value = false
          }
        })
      }
    }
  )

  function scrollToBottom() {
    nextTick(() => {
      if (!container.value || !isAutoScrolling.value) {
        return
      }

      container.value.scrollTo(0, container.value.scrollHeight)
    })
  }

  return { scrollToBottom }
}

import twilio from 'twilio'

export class Twilio {
  private client: twilio.Twilio
  private accountSid = 'AC9400af563ea46b42b3255f287abXXXXX'
  private authToken = '65406c430c90d00268ef9bf0720XXXXX'
  private verifyServiceSid = 'VAaa47973652ccaabfc582ed8c1afXXXXX'

  constructor() {
    this.client = twilio(this.accountSid, this.authToken)
  }

  sendVerificationCode(options: { to: string }) {
    return this.client.verify.v2
      .services(this.verifyServiceSid)
      .verifications.create({ to: options.to, channel: 'sms' })
  }
  checkVerificationCode(options: { to: string; code: string }) {
    return this.client.verify.v2
      .services(this.verifyServiceSid)
      .verificationChecks.create({
        to: options.to,
        code: options.code,
      })
  }
}

import express from 'express'
import phone from 'phone'
import { Twilio } from './utils/sms'

const app = express()
app.use(express.json())

app.post('/send', async (req, res) => {
  const body = req.body as { phone?: string }
  if (!body.phone) {
    throw new Error('400')
  }

  const phoneValidation = phone(body.phone, { country: 'KOR' })
  if (!phoneValidation.isValid) {
    throw new Error('invalid format of the phone.')
  }

  const twilio = new Twilio()

  const result = await twilio.sendVerificationCode({
    to: phoneValidation.phoneNumber,
  })

  res.json({
    success: true,
    data: { result },
  })
})

app.listen(3000, () => {
  console.log('http://localhost:3000')
})

app.post('/verify', async (req, res) => {
  const body = req.body as { phone?: string; code?: string }
  if (!body.phone || !body.code) {
    throw new Error('400')
  }

  const phoneValidation = phone(body.phone, { country: 'KOR' })
  if (!phoneValidation.isValid) {
    throw new Error('invalid format of the phone.')
  }

  const twilio = new Twilio()

  const result = await twilio.checkVerificationCode({
    to: phoneValidation.phoneNumber,
    code: body.code,
  })

  res.json({
    success: true,
    data: { result },
  })
})

