        ticker.trade_price = -ticker.trade_price;
        ticker.change_price = -ticker.change_price;
      }

      ticker.change_rate = ticker.change_rate * 100;
      /** 수정된 부분 끝 */

      coins.value[ticker.market] = Object.assign(
        coins.value[ticker.market],
        ticker
      );
    });
